import React, { useState, useEffect, useRef } from "react";
import { Link, useLocation } from "react-router-dom";
import Navbar from "./navbar";
import axios from "axios";

const Home = ({ username }) => {
  const [previousSkills, setPreviousSkills] = useState([]);
  const [availableSkills, setAvailableSkills] = useState([]);
  const [selectedSkill, setSelectedSkill] = useState("");
  const [isSkillsVisible, setIsSkillsVisible] = useState(false);
  const skillsRef = useRef(null);
  const homeRef = useRef(null);
  const location = useLocation();

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => setIsSkillsVisible(entry.isIntersecting),
      { threshold: 0.25 }
    );
    if (skillsRef.current) observer.observe(skillsRef.current);
    return () => observer.disconnect();
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userDataResponse = await axios.get(`http://localhost:8000/user-data/${username}`);
        setPreviousSkills(userDataResponse.data.skills);

        const skillsResponse = await axios.get("http://localhost:8000/available-skills");
        setAvailableSkills(skillsResponse.data.skills);
      } catch (error) {
        console.error("Failed to load data:", error);
      }
    };
    if (username) {
      fetchData();
    }
  }, [username]);

  return (
    <div className="bg-black text-white min-h-screen flex flex-col items-center">
      <Navbar homeRef={homeRef} skillsRef={skillsRef} username={username} />
      <div ref={homeRef} id="home" className="flex items-center justify-center h-screen">
        <h1 className="text-9xl font-bold text-center">Learn</h1>
      </div>
      <div
        id="skills"
        ref={skillsRef}
        className={`flex flex-col items-center justify-center min-h-screen text-center pb-32 transition-opacity duration-[2000ms] ${
          isSkillsVisible ? "opacity-100" : "opacity-0"
        }`}
      >
        <h2 className="text-6xl font-extrabold mb-16">What do you yearn to learn?</h2>
        <select
          value={selectedSkill}
          onChange={(e) => setSelectedSkill(e.target.value)}
          className="p-2 bg-gray-700 text-white rounded-md mb-4"
        >
          <option value="">Select a skill</option>
          {availableSkills.map((skill, index) => (
            <option key={index} value={skill}>{skill}</option>
          ))}
        </select>
        {selectedSkill && (
          <Link
            to={`/quiz/${selectedSkill}`}
            className="ml-4 px-4 py-2 bg-green-500 text-white rounded-md"
          >
            Start Quiz
          </Link>
        )}
        <h3 className="text-4xl font-bold mt-8">Previous Learnings</h3>
        {previousSkills.length > 0 ? (
          <div className="space-y-4 mt-4">
            {previousSkills.map((skill, index) => (
              <div key={index} className="bg-gray-800 p-4 rounded-md shadow-md">
                <h4 className="text-2xl font-semibold">{skill.skill}</h4>
                <p>Score: {skill.score}</p>
                <p>Level: {skill.learning_journey.level}</p>
                <p>Progress: {skill.progress.toFixed(2)}%</p>
                <Link
                  to={`/quiz/${skill.skill}`}
                  className="mt-2 inline-block px-4 py-2 bg-blue-500 text-white rounded-md"
                >
                  Continue Learning
                </Link>
              </div>
            ))}
          </div>
        ) : (
          <p>No previous skills found.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
