import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Home from "./home";
import Quiz from "./Quiz";
import About from "./about";
import Navbar from "./navbar";
import Profile from "./Profile";
import Dashboard from "./Dashboard";
import UsernamePrompt from "./UsernamePrompt";
import LearningJourney from "./LearningJourney";
import "./index.css";

function App() {
  const [username, setUsername] = useState(localStorage.getItem("username"));

  // ✨ Glowing cursor logic with trailing particles
  useEffect(() => {
    const cursor = document.createElement("div");
    cursor.classList.add("custom-cursor");
    document.body.appendChild(cursor);

    const moveCursor = (e) => {
      // Update main cursor position
      cursor.style.left = `${e.clientX}px`;
      cursor.style.top = `${e.clientY}px`;

      // Create particle
      const particle = document.createElement("div");
      particle.classList.add("cursor-particle");
      particle.style.left = `${e.clientX}px`;
      particle.style.top = `${e.clientY}px`;

      const angle = Math.random() * 2 * Math.PI;
      const distance = Math.random() * 25 + 5; // 5px to 30px
      const xMove = Math.cos(angle) * distance;
      const yMove = Math.sin(angle) * distance;

      // Animate movement + fade
      requestAnimationFrame(() => {
        particle.style.transform = `translate(${xMove}px, ${yMove}px) scale(0.3)`;
        particle.style.opacity = "0";
      });

      document.body.appendChild(particle);

      // Remove after fade
      setTimeout(() => {
        particle.remove();
      }, 500);
    };

    window.addEventListener("mousemove", moveCursor);

    return () => {
      window.removeEventListener("mousemove", moveCursor);
      cursor.remove();
    };
  }, []);

  return (
    <Router>
      {!username && <UsernamePrompt setUsername={setUsername} />}
      <Routes>
        <Route path="/" element={<Home username={username} />} />
        <Route path="/quiz/:skill" element={<Quiz username={username} />} />
        <Route path="/learning-journey/:skill" element={<LearningJourney username={username} />} />
        <Route path="/about" element={<About />} />
        <Route path="/profile" element={<Profile username={username} />} />
        <Route path="/dashboard" element={<Dashboard username={username} />} />
      </Routes>
    </Router>
  );
}

export default App;
