import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

export default function Dashboard() {
  const [skills, setSkills] = useState([]);

  useEffect(() => {
    const fetchSkills = async () => {
      const username = localStorage.getItem("username");
      const res = await axios.get(`http://localhost:8000/user-data/${username}`);
      setSkills(res.data.skills || []);
    };
    fetchSkills();
  }, []);

  return (
    <div className="min-h-screen bg-black text-white p-10">
      <h1 className="text-4xl font-extrabold mb-8">Dashboard</h1>
      {skills.map((skill, index) => (
        <div
          key={index}
          className="bg-gray-900 border border-gray-700 p-6 rounded-lg mb-6 shadow-md hover:shadow-xl transition"
        >
          <h2 className="text-2xl font-bold text-blue-400">{skill.skill}</h2>
          <p className="mt-2">Score: {skill.score}</p>
          <p>Level: {skill.learning_journey.level}</p>
          <p>Progress: {skill.progress.toFixed(2)}%</p>
          <Link
            to={`/learning-journey/${skill.skill}`}
            className="mt-4 inline-block px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-md"
          >
            Continue Learning
          </Link>
        </div>
      ))}
    </div>
  );
}
