import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "./navbar;" // Import Navbar

const Quiz = () => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [selectedOption, setSelectedOption] = useState(null);
    const [userAnswers, setUserAnswers] = useState([]);
    const [score, setScore] = useState(0);
    const [showScore, setShowScore] = useState(false);

    // ✅ Generate or Retrieve User ID from Local Storage
    const getUserId = () => {
        let storedUserId = localStorage.getItem("user_id");
        if (!storedUserId) {
            storedUserId = "user_" + Math.random().toString(36).substr(2, 9);
            localStorage.setItem("user_id", storedUserId);
        }
        return storedUserId;
    };
    const userId = getUserId();

    // ✅ Fetch questions from API
    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await axios.get("http://localhost:8000/questions");
                if (response.data.length > 0) {
                    setQuestions(response.data);
                    setUserAnswers(new Array(response.data.length).fill(null));
                } else {
                    console.error("No questions available");
                }
            } catch (error) {
                console.error("Error fetching questions:", error);
            }
        };
        fetchQuestions();
    }, []);

    const handleOptionSelect = (option) => {
        setSelectedOption(option);
        const updatedAnswers = [...userAnswers];
        updatedAnswers[currentQuestion] = option;
        setUserAnswers(updatedAnswers);
    };

    const handleNextQuestion = () => {
        if (selectedOption !== null) {
            let isCorrect = selectedOption === questions[currentQuestion].correct_answer;

            setScore(prevScore => {
                let newScore = isCorrect ? prevScore + 1 : prevScore;

                if (currentQuestion + 1 < questions.length) {
                    setCurrentQuestion(currentQuestion + 1);
                    setSelectedOption(userAnswers[currentQuestion + 1]);
                } else {
                    setShowScore(true);
                    submitScore(newScore);
                }

                return newScore;
            });
        } else {
            alert("Please select an option before proceeding.");
        }
    };

    const handlePreviousQuestion = () => {
        if (currentQuestion > 0) {
            setCurrentQuestion(currentQuestion - 1);
            setSelectedOption(userAnswers[currentQuestion - 1]);
        }
    };

    // ✅ Submit Score to Backend
    const submitScore = async (finalScore) => {
        try {
            const response = await axios.post("http://localhost:8000/submit-score", {
                user_id: userId,
                score: finalScore
            });
            console.log("Score submitted:", response.data);
        } catch (error) {
            console.error("Error submitting score:", error);
        }
    };

    return (
        <div className="min-h-screen bg-black text-white">
            <Navbar /> {/* Include Navbar at the top */}

            <div className="flex items-center justify-center min-h-screen">
                <div className="max-w-2xl w-full mx-auto p-6 bg-gray-900 shadow-lg rounded-lg">
                    {showScore ? (
                        <div className="text-center">
                            <h2 className="text-2xl font-bold">Your Score: {score} / {questions.length}</h2>
                        </div>
                    ) : questions.length > 0 ? (
                        <div>
                            <h2 className="text-xl font-semibold mb-2">Question {currentQuestion + 1}:</h2>
                            <p className="text-lg">{questions[currentQuestion].question}</p>

                            {questions[currentQuestion].options.map((option, index) => (
                                <div key={index} className="mt-2">
                                    <input
                                        type="radio"
                                        id={`option${index}`}
                                        name="quiz"
                                        value={option}
                                        checked={selectedOption === option}
                                        onChange={() => handleOptionSelect(option)}
                                        className="mr-2"
                                    />
                                    <label htmlFor={`option${index}`} className="cursor-pointer">{option}</label>
                                </div>
                            ))}

                            <div className="mt-4 flex justify-between">
                                <button 
                                    className="px-4 py-2 bg-gray-500 text-white rounded-md disabled:opacity-50"
                                    onClick={handlePreviousQuestion} 
                                    disabled={currentQuestion === 0}
                                >
                                    Back
                                </button>
                                <button 
                                    className="px-4 py-2 bg-blue-500 text-white rounded-md"
                                    onClick={handleNextQuestion}
                                >
                                    {currentQuestion === questions.length - 1 ? "Submit" : "Next"}
                                </button>
                            </div>
                        </div>
                    ) : (
                        <div className="flex justify-center items-center h-screen">
                            <div className="animate-spin rounded-full h-16 w-16 border-b-4 border-blue-500"></div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Quiz;
