import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "./Navbar";
import axios from "axios";

export default function LearningJourney() {
  const { skill } = useParams();
  const username = localStorage.getItem("username");
  const [learningJourney, setLearningJourney] = useState({ chapters: [] });
  const [expandedScripts, setExpandedScripts] = useState({});

  useEffect(() => {
    const fetchJourney = async () => {
      const res = await axios.get(`http://localhost:8000/user-data/${username}`);
      const skillData = res.data.skills.find(s => s.skill === skill);
      if (skillData) setLearningJourney(skillData.learning_journey);
    };
    fetchJourney();
  }, [skill, username]);

  const toggleScriptExpansion = (index) => {
    setExpandedScripts(prev => ({ ...prev, [index]: !prev[index] }));
  };

  const handleProgressUpdate = async (index, completed) => {
    await axios.post("http://localhost:8000/update-progress", {
      username,
      skill,
      chapter_index: index,
      completed
    });
    setLearningJourney(prev => {
      const updated = { ...prev };
      updated.chapters[index].completed = completed;
      return updated;
    });
  };

  return (
    <div className="min-h-screen bg-black text-white pt-28 pb-20 px-4 flex flex-col items-center">
      <Navbar />
      <div className="w-full max-w-5xl space-y-16">
        <h2 className="text-5xl font-bold text-center text-purple-400 mb-6">
          Learning Journey - {learningJourney.level} ({skill})
        </h2>
        {learningJourney.chapters.map((chapter, index) => (
          <div
            key={index}
            className="bg-gray-900 border border-gray-800 p-10 rounded-3xl shadow-xl hover:shadow-2xl transition text-center"
          >
            <h3 className="text-3xl font-extrabold text-blue-300">
              Chapter {chapter.chapter}: {chapter.title}
            </h3>
            <p className="mt-4 text-xl text-gray-300">{chapter.description}</p>
            <p className="mt-3 text-lg">Topics: <span className="text-gray-400">{chapter.topics.join(", ")}</span></p>
            <div className="mt-4 text-left">
              <strong className="text-2xl">Script:</strong>
              <button
                onClick={() => toggleScriptExpansion(index)}
                className="ml-4 text-lg text-purple-400 underline hover:text-purple-300"
              >
                {expandedScripts[index] ? "Hide" : "Show"}
              </button>
              <p className="mt-4 whitespace-pre-wrap text-lg text-gray-200">
                {expandedScripts[index]
                  ? chapter.script
                  : `${chapter.script.substring(0, 200)}...`}
              </p>
            </div>
            <p className="mt-4 text-lg text-gray-400">
              <strong>Summary:</strong> {chapter.summary}
            </p>
            <button
              onClick={() => handleProgressUpdate(index, !chapter.completed)}
              className={`mt-6 px-6 py-3 text-xl rounded-lg text-white ${chapter.completed ? "bg-yellow-600 hover:bg-yellow-700" : "bg-green-600 hover:bg-green-700"}`}
            >
              {chapter.completed ? "Mark as Incomplete" : "Mark as Completed"}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
